            // REPLACE: Tokens
            foreach (var section in newSections)
            {
                section.PageSectionTypeBody = Regex.Replace(section.PageSectionTypeBody, @"-\d{13}-", "-<componentStamp>-");

                section.PageSectionTypeBody = ResolveDuplicateIdentifiers(section.PageSectionTypeBody);
            }

            // REPLACE: Updated Templates
            foreach (var existingSection in context.PageSectionTypes.ToList())
            {
                 var matchedTemplate = newSections.FirstOrDefault(x => x.PageSectionTypeName == existingSection.PageSectionTypeName);

                if (matchedTemplate == null || existingSection.PageSectionTypeBody != matchedTemplate.PageSectionTypeBody || existingSection.PageSectionTypeOrder != matchedTemplate.PageSectionTypeOrder)
                {
                    context.PageSectionTypes.Remove(existingSection);
                }
                else
                {
                    newSections.Remove(matchedTemplate);
                }
            }

            context.PageSectionTypes.AddRange(newSections);
        }

        private static string ResolveDuplicateIdentifiers(string htmlBody)
        {
            var document = new HtmlDocument();

            document.LoadHtml(htmlBody);

            var uniqueElementId = 0;

            foreach (var child in document.DocumentNode.Descendants())
            {
                if (child.NodeType != HtmlNodeType.Element)
                    continue;

                uniqueElementId += 1;

                if (child.Name.Equals("section", System.StringComparison.OrdinalIgnoreCase))
                {
                    child.SetAttributeValue("id", $"section-<sectionId>");
                }
                else
                {
                    child.SetAttributeValue("id", $"element-{uniqueElementId}-<componentStamp>-<sectionId>");
                }
            }

            return document.DocumentNode.OuterHtml;
        }
